mapper.select:
SELECT * FROM users
WHERE 1=1

  AND id = #{Id,jdbcType=INTEGER}


  AND name = #{Name,jdbcType=VARCHAR}


  AND password = #{Password,jdbcType=VARCHAR}


  AND administrative = #{Administrative,jdbcType=INTEGER}

select 

from users
where id = #{id,jdbcType=INTEGER}
  and name = #{name,jdbcType=VARCHAR}
  and password = #{password,jdbcType=VARCHAR}

mapper.delete:
delete from users
where id = #{id,jdbcType=INTEGER}
  and name = #{name,jdbcType=VARCHAR}
  and password = #{password,jdbcType=VARCHAR}

mapper.update:
update users


    administrative = #{administrative,jdbcType=INTEGER},


where id = #{id,jdbcType=INTEGER}
  and name = #{name,jdbcType=VARCHAR}
  and password = #{password,jdbcType=VARCHAR}

update users
set administrative = #{administrative,jdbcType=INTEGER}
where id = #{id,jdbcType=INTEGER}
  and name = #{name,jdbcType=VARCHAR}
  and password = #{password,jdbcType=VARCHAR}
